import java.util.*;
public class Main{
  public static void main(String[] args){
    Scanner sc=new Scanner(System.in);
    double a=sc.nextDouble();
    double b=sc.nextDouble();
    String op=sc.next();
    double ans=0;
    switch(op){
      case "+": ans=a+b; break;
      case "-": ans=a-b; break;
      case "*": ans=a*b; break;
      case "/": ans=a/b; break;
      case "%": ans=a%b; break;
    }
    System.out.println(ans);
  }
}

