import java.util.*;
public class Main{
    public static void main(String[] args){
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        if(n < 2){ System.out.println(false); return; }
        boolean ok = true;
        int i = 2;
        while(i * i <= n){
            if(n % i == 0){ ok = false; break; }
            i++;
        }
        System.out.println(ok);
    }
}

