import java.util.*;
public class Main{
    public static void main(String[] args){
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        for(int i=1;i<=n;i++){
            StringBuilder sb = new StringBuilder();
            for(int j=1;j<=n;j++){
                sb.append(i).append("+").append(j).append("=").append(i+j);
                if(j < n) sb.append(" ");
            }
            System.out.println(sb.toString());
        }
    }
}
